import React, { useState } from 'react';
import styled, { createGlobalStyle, keyframes } from 'styled-components';
import { Header } from './components/Header';
import { ShortenerForm } from './components/ShortenerForm';
import { ResultCard } from './components/ResultCard';
import { Footer } from './components/Footer';

const GlobalStyles = createGlobalStyle`
  :root {
    --bg: #0b1020;
    --card: #0f172a;
    --primary: #6ee7ff;
    --accent: #a78bfa;
    --text: #e2e8f0;
    --muted: #94a3b8;
  }
  * { box-sizing: border-box; }
  html, body, #root { height: 100%; }
  body {
    margin: 0;
    font-family: 'Inter', system-ui, -apple-system, Segoe UI, Roboto, Arial, sans-serif;
    color: var(--text);
    background: radial-gradient(1200px 800px at 75% -100px, rgba(167,139,250,0.25), transparent 60%),
                radial-gradient(1000px 700px at -10% 10%, rgba(110,231,255,0.2), transparent 60%),
                var(--bg);
    overflow-x: hidden;
  }
`;

const float = keyframes`
  0% { transform: translateY(0) }
  50% { transform: translateY(-8px) }
  100% { transform: translateY(0) }
`;

const Container = styled.main`
  min-height: 100%;
  display: grid;
  grid-template-rows: auto 1fr auto;
`;

const CanvasGlow = styled.div`
  position: fixed;
  inset: -20% -30% auto auto;
  width: 60vw;
  height: 60vh;
  background: radial-gradient(circle at 30% 30%, rgba(167,139,250,0.25), transparent 45%),
              radial-gradient(circle at 70% 60%, rgba(110,231,255,0.18), transparent 50%);
  filter: blur(40px) saturate(120%);
  pointer-events: none;
  opacity: 0.8;
  mix-blend-mode: screen;
  animation: ${float} 9s ease-in-out infinite;
`;

const Content = styled.section`
  display: grid;
  align-content: start;
  gap: 24px;
  padding: 48px 20px 28px;
  max-width: 980px;
  margin: 0 auto;
  width: 100%;
`;

export default function App() {
  const [longUrl, setLongUrl] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  async function handleShorten() {
    setError('');
    setLoading(true);
    setResult(null);
    try {
      // Placeholder: will call backend later
      await new Promise(r => setTimeout(r, 900));
      const id = Math.random().toString(36).slice(2, 8);
      const shortened = `${window.location.origin}/${id}`;
      setResult({ original: longUrl, shortened });
    } catch (e) {
      setError('Something went wrong. Please try again.');
    } finally {
      setLoading(false);
    }
  }

  return (
    <Container>
      <GlobalStyles />
      <CanvasGlow />
      <Header />
      <Content>
        <ShortenerForm
          value={longUrl}
          onChange={setLongUrl}
          onSubmit={handleShorten}
          loading={loading}
          error={error}
        />
        {result && <ResultCard original={result.original} shortened={result.shortened} />}
      </Content>
      <Footer />
    </Container>
  );
}
