import React from 'react';
import styled, { keyframes } from 'styled-components';

const breathe = keyframes`
  from { box-shadow: 0 0 0 0 rgba(167,139,250,0.3); }
  to { box-shadow: 0 0 0 14px rgba(167,139,250,0); }
`;

const Card = styled.section`
  background: linear-gradient(180deg, rgba(15,23,42,0.8), rgba(15,23,42,0.6));
  border: 1px solid rgba(148,163,184,0.15);
  border-radius: 16px;
  padding: 22px;
  position: relative;
  overflow: hidden;
  box-shadow: 0 10px 30px rgba(2,6,23,0.6);
`;

const Title = styled.h1`
  margin: 0 0 8px;
  font-size: 28px;
`;

const Subtitle = styled.p`
  margin: 0 0 18px;
  color: var(--muted);
`;

const Row = styled.div`
  display: grid;
  grid-template-columns: 1fr auto;
  gap: 12px;
`;

const Input = styled.input`
  height: 52px;
  border-radius: 12px;
  border: 1px solid rgba(148,163,184,0.25);
  outline: none;
  padding: 0 16px;
  font-size: 16px;
  color: var(--text);
  background: rgba(2,6,23,0.6);
  transition: border-color 0.2s ease, box-shadow 0.2s ease;
  &:focus {
    border-color: #a78bfa;
    box-shadow: 0 0 0 3px rgba(167,139,250,0.25);
  }
`;

const Button = styled.button`
  height: 52px;
  padding: 0 20px;
  border-radius: 12px;
  border: none;
  font-weight: 700;
  color: #0b1020;
  background: linear-gradient(135deg, #22d3ee, #a78bfa);
  cursor: pointer;
  transition: transform 0.08s ease, filter 0.2s ease;
  &:active { transform: translateY(1px); }
  &:disabled { filter: grayscale(0.4) brightness(0.7); cursor: not-allowed; }
`;

const Ring = styled.div`
  position: absolute;
  right: -30px;
  top: -30px;
  width: 160px;
  height: 160px;
  border-radius: 50%;
  border: 3px solid rgba(167,139,250,0.35);
  animation: ${breathe} 2.6s ease-out infinite alternate;
`;

const ErrorText = styled.div`
  margin-top: 10px;
  color: #fca5a5;
  font-weight: 600;
`;

export function ShortenerForm({ value, onChange, onSubmit, loading, error }) {
  return (
    <Card>
      <Ring />
      <Title>Shorten your long links</Title>
      <Subtitle>Paste any long URL and get a beautiful short link instantly.</Subtitle>
      <Row>
        <Input
          value={value}
          onChange={(e) => onChange(e.target.value)}
          placeholder="https://example.com/very/long/link..."
        />
        <Button onClick={onSubmit} disabled={!value || loading}>
          {loading ? 'Shortening…' : 'Shorten URL'}
        </Button>
      </Row>
      {!!error && <ErrorText>{error}</ErrorText>}
    </Card>
  );
}
