import React, { useEffect, useState } from 'react';
import { Header } from './components/Header';
import { ShortenerForm } from './components/ShortenerForm';
import { ResultCard } from './components/ResultCard';
import { Footer } from './components/Footer';
import { HistoryList } from './components/HistoryList';

export default function App() {
  const [longUrl, setLongUrl] = useState('');
  const [latestResult, setLatestResult] = useState(null);
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    try {
      const raw = localStorage.getItem('url_short_history');
      if (raw) {
        const parsed = JSON.parse(raw);
        if (Array.isArray(parsed)) setHistory(parsed);
      }
    } catch {}
  }, []);

  useEffect(() => {
    try {
      localStorage.setItem('url_short_history', JSON.stringify(history));
    } catch {}
  }, [history]);

  async function handleShorten() {
    setError('');
    setLoading(true);
    setLatestResult(null);
    try {
      // Placeholder: will call backend later
      await new Promise(r => setTimeout(r, 900));
      const id = Math.random().toString(36).slice(2, 8);
      const shortened = `${window.location.origin}/${id}`;
      const entry = { id, original: longUrl, shortened, createdAt: Date.now() };
      setLatestResult(entry);
      setHistory((prev) => [entry, ...prev].slice(0, 50));
    } catch (e) {
      setError('Something went wrong. Please try again.');
    } finally {
      setLoading(false);
    }
  }

  return (
    <main className="container">
      <div className="glow" />
      <Header />
      <section className="content">
        <ShortenerForm
          value={longUrl}
          onChange={setLongUrl}
          onSubmit={handleShorten}
          loading={loading}
          error={error}
        />
        {latestResult && <ResultCard original={latestResult.original} shortened={latestResult.shortened} />}
        {history.length > 0 && <HistoryList items={history} />}
      </section>
      <Footer />
    </main>
  );
}
